import"../styles/styles.scss";var hamburgerMenu=document.querySelector(".hamburger"),closeButton=document.querySelector(".close-button"),sidebar=document.querySelector(".sidebar");function showNav(){sidebar.classList.remove("hidden"),closeButton.classList.remove("hidden")}function closeNav(){sidebar.classList.add("hidden"),closeButton.classList.add("hidden")}hamburgerMenu.addEventListener("click",showNav),closeButton.addEventListener("click",closeNav);